sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

isDateToday: function(strDate){
	  //Create GlideDateTime objects for the current date and the due date
	   var rightnow1 = new GlideDateTime();
	   var testdate1 = new GlideDateTime(strDate);
	//Get the date portion of the date\times
	  var today1 = rightnow1.getDate();
	  var istoday1 = testdate1.getDate();

//Compare the two values
	  if (today1.compareTo(istoday1) == 0){
		     return true;
	  }
		  else {
			return false;
		}
	  },

    type: 'NeedItUtils'
};
